   609	<script>
   610	    document.addEventListener('DOMContentLoaded', function() {
   611	        const pageContainer = document.getElementById('page-container');
   612	        const pages = document.querySelectorAll('.page');
   613	        const progressDots = document.getElementById('progress-dots');
   614	        const swipeHint = document.querySelector('.swipe-hint');
   615	        let currentPage = 1;
   616	        let startX, startY;
   617	        let initialTouchX, initialTouchY;
   618	        let isAnimating = false;
   619	
   620	        // 创建进度点
   621	        for (let i = 0; i < pages.length; i++) {
   622	            const dot = document.createElement('div');
   623	            dot.classList.add('dot');
   624	            if (i === 0) dot.classList.add('active');
   625	            progressDots.appendChild(dot);
   626	
   627	            // 点击进度点跳转
   628	            dot.addEventListener('click', () => {
   629	                if (!isAnimating) {
   630	                    goToPage(i + 1);
   631	                }
   632	            });
   633	        }
   634	
   635	        const dots = document.querySelectorAll('.dot');
   636	
   637	        // 初始化页面
   638	        function initPages() {
   639	            pageContainer.style.width = `${pages.length * 100}%`;
   640	            pages.forEach(page => {
   641	                page.style.width = `${100 / pages.length}%`;
   642	            });
   643	
   644	            // 激活第一页
   645	            setTimeout(() => {
   646	                pages[0].classList.add('active');
   647	            }, 100);
   648	
   649	            // 3秒后隐藏滑动提示
   650	            setTimeout(() => {
   651	                swipeHint.style.opacity = '0';
   652	                swipeHint.style.transition = 'opacity 1s ease';
   653	            }, 5000);
   654	        }
   655	
   656	        // 翻页函数
   657	        function goToPage(pageNum) {
   658	            if (pageNum < 1 || pageNum > pages.length || isAnimating) return;
   659	
   660	            isAnimating = true;
   661	
   662	            // 更新页面位置
   663	            const offset = (pageNum - 1) * -100;
   664	            pageContainer.style.transform = `translateX(${offset}%)`;
   665	
   666	            // 更新活动状态
   667	            pages.forEach((page, index) => {
   668	                if (index + 1 === pageNum) {
   669	                    page.classList.add('active');
   670	                } else {
   671	                    page.classList.remove('active');
   672	                }
   673	
   674	                dots[index].classList.toggle('active', index + 1 === pageNum);
   675	            });
   676	
   677	            currentPage = pageNum;
   678	
   679	            // 根据页码触发特定动画
   680	            if (pageNum === 2) {
   681	                initCounters();
   682	            } else if (pageNum === 5) {
   683	                createFloatingHearts();
   684	            } else if (pageNum === 7) {
   685	                typeWriterEffect();
   686	            } else if (pageNum === 8) {
   687	                createConfetti();
   688	            }
   689	
   690	            // 动画完成后解锁
   691	            setTimeout(() => {
   692	                isAnimating = false;
   693	            }, 800);
   694	        }
   695	
   696	        // 数字增长动画
   697	        function animateCounter(id, start, end, duration) {
   698	            const element = document.getElementById(id);
   699	            if (!element) return;
   700	
   701	            let startTime = null;
   702	
   703	            function animate(timestamp) {
   704	                if (!startTime) startTime = timestamp;
   705	                const progress = Math.min((timestamp - startTime) / duration, 1);
   706	                const value = Math.floor(progress * (end - start) + start);
   707	                element.textContent = value.toLocaleString();
   708	
   709	                if (progress < 1) {
   710	                    requestAnimationFrame(animate);
   711	                } else {
   712	                    element.classList.add('pulse');
   713	                    setTimeout(() => {
   714	                        element.classList.remove('pulse');
   715	                    }, 1000);
   716	                }
   717	            }
   718	
   719	            requestAnimationFrame(animate);
   720	        }
   721	
   722	        // 初始化计数器动画
   723	        function initCounters() {
   724	            // 计算天数
   725	            const startDate = new Date('2023-05-21');
   726	            const endDate = new Date('2023-07-29');
   727	            const daysDiff = Math.floor((endDate - startDate) / (1000 * 60 * 60 * 24)) + 1;
   728	
   729	            // 计算平均消息数
   730	            const totalMessages = 17140;
   731	            const avgMessages = Math.round(totalMessages / daysDiff);
   732	
   733	            // 第2页计数器
   734	            setTimeout(() => {
   735	                animateCounter('days-count', 0, daysDiff, 1500);
   736	                animateCounter('total-messages', 0, totalMessages, 2000);
   737	                animateCounter('avg-messages', 0, avgMessages, 1500);
   738	            }, 500);
   739	
   740	            // 第3页计数器
   741	            if (currentPage === 3) {
   742	                setTimeout(() => {
   743	                    animateCounter('text-messages', 0, 13567, 1500);
   744	                    animateCounter('emoji-messages', 0, 2168, 1500);
   745	                    animateCounter('image-messages', 0, 610, 1500);
   746	                }, 500);
   747	            }
   748	
   749	            // 第4页计数器
   750	            if (currentPage === 4) {
   751	                setTimeout(() => {
   752	                    animateCounter('call-count', 0, 20, 1000);
   753	                    animateCounter('call-minutes', 0, 1531, 2000);
   754	                }, 500);
   755	            }
   756	
   757	            // 第5页计数器
   758	            if (currentPage === 5) {
   759	                setTimeout(() => {
   760	                    animateCounter('love-count', 0, 103, 1500);
   761	                    animateCounter('miss-count', 0, 64, 1500);
   762	                    animateCounter('haha-count', 0, 570, 2000);
   763	                    animateCounter('a-count', 0, 343, 2000);
   764	                    animateCounter('good-count', 0, 162, 2000);
   765	                }, 500);
   766	            }
   767	
   768	            // 第7页计数器
   769	            if (currentPage === 7) {
   770	                setTimeout(() => {
   771	                    animateCounter('day1-count', 0, 691, 1500);
   772	                    animateCounter('day2-count', 0, 595, 1500);
   773	                    animateCounter('day3-count', 0, 580, 1500);
   774	                }, 500);
   775	            }
   776	        }
   777	
   778	        // 创建浮动爱心
   779	        function createFloatingHearts() {
   780	            const container = document.getElementById('floating-hearts');
   781	            if (!container) return;
   782	
   783	            container.innerHTML = '';
   784	
   785	            const createHeart = () => {
   786	                const heart = document.createElement('div');
   787	                heart.classList.add('floating-heart');
   788	                heart.innerHTML = '❤️';
   789	                heart.style.left = `${Math.random() * 100}%`;
   790	                heart.style.animationDelay = `${Math.random() * 2}s`;
   791	                container.appendChild(heart);
   792	
   793	                setTimeout(() => {
   794	                    heart.remove();
   795	                }, 4000);
   796	            };
   797	
   798	            // 初始创建10个心
   799	            for (let i = 0; i < 10; i++) {
   800	                setTimeout(() => {
   801	                    createHeart();
   802	                }, i * 300);
   803	            }
   804	
   805	            // 每隔一段时间创建新的心
   806	            const heartInterval = setInterval(() => {
   807	                if (currentPage !== 5) {
   808	                    clearInterval(heartInterval);
   809	                    return;
   810	                }
   811	                createHeart();
   812	            }, 1000);
   813	        }
   814	
   815	        // 打字机效果
   816	        function typeWriterEffect() {
   817	            const element = document.getElementById('late-message');
   818	            if (!element) return;
   819	
   820	            const text = "赛里木湖的夜晚是真冷";
   821	            element.textContent = "";
   822	            element.classList.remove('typing-effect');
   823	
   824	            let i = 0;
   825	            const typeWriter = () => {
   826	                if (i < text.length) {
   827	                    element.textContent += text.charAt(i);
   828	                    i++;
   829	                    setTimeout(typeWriter, 100);
   830	                } else {
   831	                    element.classList.add('typing-effect');
   832	                }
   833	            };
   834	
   835	            setTimeout(typeWriter, 800);
   836	        }
   837	
   838	        // 创建彩色纸屑
   839	        function createConfetti() {
   840	            const container = document.getElementById('confetti-container');
   841	            if (!container) return;
   842	
   843	            container.innerHTML = '';
   844	
   845	            const colors = ['#ff6b81', '#5f27cd', '#ff9ff3', '#feca57', '#48dbfb'];
   846	
   847	            for (let i = 0; i < 50; i++) {
   848	                const confetti = document.createElement('div');
   849	                confetti.classList.add('confetti');
   850	                confetti.style.left = `${Math.random() * 100}%`;
   851	                confetti.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
   852	                confetti.style.width = `${5 + Math.random() * 10}px`;
   853	                confetti.style.height = `${5 + Math.random() * 10}px`;
   854	                confetti.style.animationDelay = `${Math.random() * 3}s`;
   855	                confetti.style.animationDuration = `${3 + Math.random() * 4}s`;
   856	
   857	                container.appendChild(confetti);
   858	            }
   859	        }
   860	
   861	        // 触摸事件处理
   862	        function handleTouchStart(e) {
   863	            startX = e.touches[0].clientX;
   864	            startY = e.touches[0].clientY;
   865	            initialTouchX = startX;
   866	            initialTouchY = startY;
   867	        }
   868	
   869	        function handleTouchMove(e) {
   870	            if (!startX || !startY) return;
   871	
   872	            const currentX = e.touches[0].clientX;
   873	            const currentY = e.touches[0].clientY;
   874	
   875	            // 计算水平和垂直移动距离
   876	            const diffX = startX - currentX;
   877	            const diffY = startY - currentY;
   878	
   879	            // 如果垂直滑动大于水平滑动，不处理
   880	            if (Math.abs(diffY) > Math.abs(diffX)) return;
   881	
   882	            // 防止页面滚动
   883	            e.preventDefault();
   884	
   885	            // 计算总移动距离占屏幕宽度的百分比
   886	            const movePercent = (diffX / window.innerWidth) * 100;
   887	
   888	            // 计算当前页面偏移量
   889	            const currentOffset = (currentPage - 1) * -100;
   890	
   891	            // 设置页面容器的位置，添加阻尼效果
   892	            const damping = 0.3; // 阻尼系数
   893	            const newOffset = currentOffset - movePercent * damping;
   894	
   895	            // 限制拖动范围
   896	            if ((currentPage === 1 && diffX < 0) || (currentPage === pages.length && diffX > 0)) {
   897	                // 第一页向右拖或最后一页向左拖时，增加更强的阻尼
   898	                pageContainer.style.transform = `translateX(${currentOffset - movePercent * 0.1}%)`;
   899	            } else {
   900	                pageContainer.style.transform = `translateX(${newOffset}%)`;
   901	            }
   902	        }
   903	
   904	        function handleTouchEnd(e) {
   905	            if (!startX) return;
   906	
   907	            const currentX = e.changedTouches[0].clientX;
   908	            const diffX = startX - currentX;
   909	
   910	            // 重置页面位置
   911	            const threshold = window.innerWidth * 0.15; // 15% 的屏幕宽度作为阈值
   912	
   913	            if (diffX > threshold && currentPage < pages.length) {
   914	                // 向左滑，前进一页
   915	                goToPage(currentPage + 1);
   916	            } else if (diffX < -threshold && currentPage > 1) {
   917	                // 向右滑，后退一页
   918	                goToPage(currentPage - 1);
   919	            } else {
   920	                // 回到当前页
   921	                goToPage(currentPage);
   922	            }
   923	
   924	            // 重置起始位置
   925	            startX = null;
   926	            startY = null;
   927	        }
   928	
   929	        // 监听页面变化，触发相应动画
   930	        function handlePageChange() {
   931	            if (currentPage === 3) {
   932	                setTimeout(() => {
   933	                    animateCounter('text-messages', 0, 13567, 1500);
   934	                    animateCounter('emoji-messages', 0, 2168, 1500);
   935	                    animateCounter('image-messages', 0, 610, 1500);
   936	                }, 500);
   937	            } else if (currentPage === 4) {
   938	                setTimeout(() => {
   939	                    animateCounter('call-count', 0, 20, 1000);
   940	                    animateCounter('call-minutes', 0, 1531, 2000);
   941	                }, 500);
   942	            } else if (currentPage === 5) {
