   970	            mutations.forEach((mutation) => {
   971	                if (mutation.type === 'attributes' && mutation.attributeName === 'class') {
   972	                    if (mutation.target.classList.contains('active')) {
   973	                        const pageIndex = Array.from(pages).indexOf(mutation.target);
   974	                        if (pageIndex + 1 !== currentPage) {
   975	                            currentPage = pageIndex + 1;
   976	                            handlePageChange();
   977	                        }
   978	                    }
   979	                }
   980	            });
   981	        });
   982	
   983	        pages.forEach(page => {
   984	            pageObserver.observe(page, { attributes: true });
   985	        });
   986	
   987	        // 初始化
   988	        initPages();
   989	    });
