                transform: translateY(-10px) rotate(0deg);
                opacity: 1;
            }
            100% {
                transform: translateY(100vh) rotate(360deg);
                opacity: 0;
            }
        }

        @keyframes pulse {
            0%, 100% { transform: scale(1); }
            50% { transform: scale(1.1); }
        }

        @keyframes shake {
            0%, 100% { transform: translateX(0); }
            10%, 30%, 50%, 70%, 90% { transform: translateX(-5px); }
            20%, 40%, 60%, 80% { transform: translateX(5px); }
        }

        .pulse {
            animation: pulse 1s ease-in-out;
        }

        .shake {
            animation: shake 0.5s ease-in-out;
        }

        .typing-effect::after {
            content: '|';
            animation: blink 1s infinite;
        }

        @keyframes blink {
            0%, 100% { opacity: 1; }
            50% { opacity: 0; }
        }

        .emoji-grid {
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            gap: 15px;
            margin: 15px 0;
        }

        .emoji-item {
            display: flex;
            flex-direction: column;
            align-items: center;
            opacity: 0;
            transform: scale(0.8);
            transition: opacity 0.5s ease, transform 0.5s ease;
        }

        .page.active .emoji-item {
            opacity: 1;
            transform: scale(1);
        }

        .page.active .emoji-item:nth-child(1) { transition-delay: 0.2s; }
        .page.active .emoji-item:nth-child(2) { transition-delay: 0.4s; }
        .page.active .emoji-item:nth-child(3) { transition-delay: 0.6s; }

        .emoji-icon {
            font-size: 2rem;
            margin-bottom: 5px;
        }

        .emoji-count {
            font-size: 0.9rem;
            font-weight: bold;
        }

        /* 移动端响应式设计 */
        @media screen and (max-width: 768px) {
            .title {
                font-size: 2rem;
                margin-bottom: 20px;
            }

            .subtitle {
                font-size: 1.2rem;
                margin-bottom: 15px;
            }

            .content {
                font-size: 1rem;
                max-width: 95%;
                margin-bottom: 20px;
            }

            .stat-card {
                padding: 15px;
