        /* 横屏适配 */
        @media screen and (orientation: landscape) and (max-height: 500px) {
            .page {
                padding: 10px;
                justify-content: flex-start;
                padding-top: 20px;
            }

            .title {
                font-size: 1.5rem;
                margin-bottom: 10px;
            }

            .subtitle {
                font-size: 1rem;
                margin-bottom: 8px;
            }

            .content {
                font-size: 0.85rem;
                margin-bottom: 15px;
            }

            .stat-card {
                padding: 8px;
                margin: 5px 0;
                max-width: 280px;
            }

            .emoji {
                font-size: 1.5rem;
