    <!-- 进度指示点 -->
    <div class="progress-dots" id="progress-dots"></div>

    <!-- 滑动提示 -->
    <div class="swipe-hint">
        <span class="swipe-arrow">←</span>
        <span>左右滑动查看</span>
        <span class="swipe-arrow">→</span>
    </div>
