<script>
    document.addEventListener('DOMContentLoaded', function() {
        const pageContainer = document.getElementById('page-container');
        const pages = document.querySelectorAll('.page');
        const progressDots = document.getElementById('progress-dots');
        const swipeHint = document.querySelector('.swipe-hint');
        let currentPage = 1;
        let startX, startY;
        let initialTouchX, initialTouchY;
        let isAnimating = false;

        // 创建进度点
        for (let i = 0; i < pages.length; i++) {
            const dot = document.createElement('div');
            dot.classList.add('dot');
            if (i === 0) dot.classList.add('active');
            progressDots.appendChild(dot);

            // 点击进度点跳转
            dot.addEventListener('click', () => {
                if (!isAnimating) {
                    goToPage(i + 1);
                }
            });
        }

        const dots = document.querySelectorAll('.dot');

        // 初始化页面
        function initPages() {
            pageContainer.style.width = `${pages.length * 100}%`;
            pages.forEach(page => {
                page.style.width = `${100 / pages.length}%`;
            });

            // 激活第一页
            setTimeout(() => {
                pages[0].classList.add('active');
            }, 100);

            // 3秒后隐藏滑动提示
            setTimeout(() => {
                swipeHint.style.opacity = '0';
                swipeHint.style.transition = 'opacity 1s ease';
            }, 5000);
        }

        // 翻页函数
        function goToPage(pageNum) {
            if (pageNum < 1 || pageNum > pages.length || isAnimating) return;

            isAnimating = true;

            // 更新页面位置，使用 transform3d 启用硬件加速
            const offset = (pageNum - 1) * -100;
            pageContainer.style.transform = `translate3d(${offset}%, 0, 0)`;

            // 更新活动状态
            pages.forEach((page, index) => {
                if (index + 1 === pageNum) {
                    // 延迟添加 active 类以获得更好的动画效果
                    setTimeout(() => {
                        page.classList.add('active');
                    }, 100);
                } else {
                    page.classList.remove('active');
                }

                dots[index].classList.toggle('active', index + 1 === pageNum);
            });

            currentPage = pageNum;

            // 根据页码触发特定动画
            setTimeout(() => {
                if (pageNum === 2) {
                    initCounters();
                } else if (pageNum === 5) {
                    createFloatingHearts();
                } else if (pageNum === 7) {
                    typeWriterEffect();
                } else if (pageNum === 8) {
                    createConfetti();
                }
            }, 200);

            // 动画完成后解锁
            setTimeout(() => {
                isAnimating = false;
            }, 800);
        }

        // 数字增长动画
        function animateCounter(id, start, end, duration) {
            const element = document.getElementById(id);
            if (!element) return;

            let startTime = null;

            function animate(timestamp) {
                if (!startTime) startTime = timestamp;
                const progress = Math.min((timestamp - startTime) / duration, 1);
                const value = Math.floor(progress * (end - start) + start);
                element.textContent = value.toLocaleString();

                if (progress < 1) {
                    requestAnimationFrame(animate);
                } else {
                    element.classList.add('pulse');
                    setTimeout(() => {
                        element.classList.remove('pulse');
                    }, 1000);
                }
            }

            requestAnimationFrame(animate);
        }

        // 初始化计数器动画
        function initCounters() {
            // 计算天数 (2025.5.21 - 2025.7.29)
            const startDate = new Date('2025-05-21');
            const endDate = new Date('2025-07-29');
            const daysDiff = Math.floor((endDate - startDate) / (1000 * 60 * 60 * 24)) + 1;

            // 计算平均消息数
            const totalMessages = 17140;
            const avgMessages = Math.round(totalMessages / (daysDiff - 1)); // 减去没聊天的一天

            // 第2页计数器
            setTimeout(() => {
                animateCounter('days-count', 0, daysDiff, 1500);
                animateCounter('total-messages', 0, totalMessages, 2000);
                animateCounter('avg-messages', 0, avgMessages, 1500);
            }, 500);

            // 第3页计数器
            if (currentPage === 3) {
                setTimeout(() => {
                    animateCounter('text-messages', 0, 13567, 1500);
                    animateCounter('emoji-messages', 0, 2168, 1500);
                    animateCounter('image-messages', 0, 610, 1500);
                }, 500);
            }

            // 第4页计数器
            if (currentPage === 4) {
                setTimeout(() => {
                    animateCounter('call-count', 0, 20, 1000);
                    animateCounter('call-minutes', 0, 1531, 2000);
                }, 500);
            }

            // 第5页计数器
            if (currentPage === 5) {
                setTimeout(() => {
                    animateCounter('love-count', 0, 103, 1500);
                    animateCounter('miss-count', 0, 64, 1500);
                    animateCounter('haha-count', 0, 570, 2000);
                    animateCounter('a-count', 0, 343, 2000);
                    animateCounter('good-count', 0, 162, 2000);
                }, 500);
            }

            // 第7页计数器
            if (currentPage === 7) {
                setTimeout(() => {
                    animateCounter('day1-count', 0, 691, 1500);
                    animateCounter('day2-count', 0, 595, 1500);
                    animateCounter('day3-count', 0, 580, 1500);
                }, 500);
            }
        }

        // 创建浮动爱心
        function createFloatingHearts() {
            const container = document.getElementById('floating-hearts');
            if (!container) return;

            container.innerHTML = '';

            const createHeart = () => {
                const heart = document.createElement('div');
                heart.classList.add('floating-heart');
                heart.innerHTML = '❤️';
                heart.style.left = `${Math.random() * 100}%`;
                heart.style.animationDelay = `${Math.random() * 2}s`;
                container.appendChild(heart);

                setTimeout(() => {
                    heart.remove();
                }, 4000);
            };

            // 初始创建10个心
            for (let i = 0; i < 10; i++) {
                setTimeout(() => {
                    createHeart();
                }, i * 300);
            }

            // 每隔一段时间创建新的心
            const heartInterval = setInterval(() => {
                if (currentPage !== 5) {
                    clearInterval(heartInterval);
                    return;
                }
                createHeart();
            }, 1000);
        }

        // 打字机效果
        function typeWriterEffect() {
            const element = document.getElementById('late-message');
            if (!element) return;

            const text = "赛里木湖的夜晚是真冷";
            element.textContent = "";
            element.classList.remove('typing-effect');

            let i = 0;
            const typeWriter = () => {
                if (i < text.length) {
                    element.textContent += text.charAt(i);
                    i++;
                    setTimeout(typeWriter, 100);
                } else {
                    element.classList.add('typing-effect');
                }
            };

            setTimeout(typeWriter, 800);
        }

        // 创建彩色纸屑
        function createConfetti() {
            const container = document.getElementById('confetti-container');
            if (!container) return;

            container.innerHTML = '';

            const colors = ['#ff6b81', '#5f27cd', '#ff9ff3', '#feca57', '#48dbfb'];

            for (let i = 0; i < 50; i++) {
                const confetti = document.createElement('div');
                confetti.classList.add('confetti');
                confetti.style.left = `${Math.random() * 100}%`;
                confetti.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
                confetti.style.width = `${5 + Math.random() * 10}px`;
                confetti.style.height = `${5 + Math.random() * 10}px`;
                confetti.style.animationDelay = `${Math.random() * 3}s`;
                confetti.style.animationDuration = `${3 + Math.random() * 4}s`;

                container.appendChild(confetti);
            }
        }

        // 触摸事件处理
        function handleTouchStart(e) {
            if (isAnimating) return;

            startX = e.touches[0].clientX;
            startY = e.touches[0].clientY;
            initialTouchX = startX;
            initialTouchY = startY;
            window.touchStartTime = Date.now();

            // 禁用过渡动画以获得更流畅的拖拽体验
            pageContainer.style.transition = 'none';
        }

        function handleTouchMove(e) {
            if (!startX || !startY || isAnimating) return;

            const currentX = e.touches[0].clientX;
            const currentY = e.touches[0].clientY;

            // 计算水平和垂直移动距离
            const diffX = initialTouchX - currentX;
            const diffY = initialTouchY - currentY;

            // 如果垂直滑动大于水平滑动，不处理
            if (Math.abs(diffY) > Math.abs(diffX) && Math.abs(diffX) < 30) return;

            // 防止页面滚动
            e.preventDefault();

            // 计算总移动距离占屏幕宽度的百分比
            const movePercent = (diffX / window.innerWidth) * 100;

            // 计算当前页面偏移量
            const currentOffset = (currentPage - 1) * -100;

            // 设置页面容器的位置，添加阻尼效果
            let damping = 1; // 正常阻尼系数
            let newOffset = currentOffset - movePercent;

            // 限制拖动范围，添加边界弹性效果
            if ((currentPage === 1 && diffX < 0) || (currentPage === pages.length && diffX > 0)) {
                // 第一页向右拖或最后一页向左拖时，增加更强的阻尼
                damping = 0.3;
                newOffset = currentOffset - movePercent * damping;
            }

            // 使用 transform3d 启用硬件加速
            pageContainer.style.transform = `translate3d(${newOffset}%, 0, 0)`;
        }

        function handleTouchEnd(e) {
            if (!startX || isAnimating) return;

            const currentX = e.changedTouches[0].clientX;
            const diffX = initialTouchX - currentX;
            const diffY = initialTouchY - e.changedTouches[0].clientY;

            // 恢复过渡动画
            pageContainer.style.transition = 'transform 0.6s cubic-bezier(0.65, 0, 0.35, 1)';

            // 计算滑动速度和距离
            const threshold = window.innerWidth * 0.2; // 20% 的屏幕宽度作为阈值
            const velocity = Math.abs(diffX) / (Date.now() - (window.touchStartTime || Date.now()));

            // 如果滑动速度足够快，降低阈值
            const effectiveThreshold = velocity > 0.5 ? threshold * 0.6 : threshold;

            if (diffX > effectiveThreshold && currentPage < pages.length) {
                // 向左滑，前进一页
                goToPage(currentPage + 1);
            } else if (diffX < -effectiveThreshold && currentPage > 1) {
                // 向右滑，后退一页
                goToPage(currentPage - 1);
            } else {
                // 回到当前页
                goToPage(currentPage);
            }

            // 重置起始位置
            startX = null;
            startY = null;
            window.touchStartTime = null;
        }

        // 监听页面变化，触发相应动画
        function handlePageChange() {
            if (currentPage === 3) {
                setTimeout(() => {
                    animateCounter('text-messages', 0, 13567, 1500);
                    animateCounter('emoji-messages', 0, 2168, 1500);
                    animateCounter('image-messages', 0, 610, 1500);
                }, 500);
            } else if (currentPage === 4) {
                setTimeout(() => {
                    animateCounter('call-count', 0, 20, 1000);
                    animateCounter('call-minutes', 0, 1531, 2000);
                }, 500);
            } else if (currentPage === 5) {
                setTimeout(() => {
                    animateCounter('love-count', 0, 103, 1500);
                    animateCounter('miss-count', 0, 64, 1500);
                    animateCounter('haha-count', 0, 570, 2000);
                    animateCounter('a-count', 0, 343, 2000);
                    animateCounter('good-count', 0, 162, 2000);
                    createFloatingHearts();
                }, 500);
            } else if (currentPage === 7) {
                setTimeout(() => {
                    animateCounter('day1-count', 0, 691, 1500);
                    animateCounter('day2-count', 0, 595, 1500);
                    animateCounter('day3-count', 0, 580, 1500);
                    typeWriterEffect();
                }, 500);
            } else if (currentPage === 8) {
                createConfetti();
            }
        }

        // 防抖函数
        function debounce(func, wait) {
            let timeout;
            return function executedFunction(...args) {
                const later = () => {
                    clearTimeout(timeout);
                    func(...args);
                };
                clearTimeout(timeout);
                timeout = setTimeout(later, wait);
            };
        }

        // 节流函数
        function throttle(func, limit) {
            let inThrottle;
            return function() {
                const args = arguments;
                const context = this;
                if (!inThrottle) {
                    func.apply(context, args);
                    inThrottle = true;
                    setTimeout(() => inThrottle = false, limit);
                }
            }
        }

        // 优化的触摸移动处理
        const throttledTouchMove = throttle(handleTouchMove, 16); // 60fps

        // 事件监听
        document.addEventListener('touchstart', handleTouchStart, { passive: false });
        document.addEventListener('touchmove', throttledTouchMove, { passive: false });
        document.addEventListener('touchend', handleTouchEnd);

        // 防止双击缩放
        let lastTouchEnd = 0;
        document.addEventListener('touchend', function (event) {
            const now = (new Date()).getTime();
            if (now - lastTouchEnd <= 300) {
                event.preventDefault();
            }
            lastTouchEnd = now;
        }, false);

        // 监听窗口大小变化，重新计算布局
        window.addEventListener('resize', debounce(() => {
            // 重新计算页面位置
            const offset = (currentPage - 1) * -100;
            pageContainer.style.transform = `translate3d(${offset}%, 0, 0)`;
        }, 250));

        // 监听页面变化
        const pageObserver = new MutationObserver((mutations) => {
            mutations.forEach((mutation) => {
                if (mutation.type === 'attributes' && mutation.attributeName === 'class') {
                    if (mutation.target.classList.contains('active')) {
                        const pageIndex = Array.from(pages).indexOf(mutation.target);
                        if (pageIndex + 1 !== currentPage) {
                            currentPage = pageIndex + 1;
                            handlePageChange();
                        }
                    }
                }
            });
        });

        pages.forEach(page => {
            pageObserver.observe(page, { attributes: true });
        });

        // 错误处理
        window.addEventListener('error', function(e) {
            console.error('页面错误:', e.error);
        });

        // 检查浏览器支持
        function checkBrowserSupport() {
            const isSupported =
                'querySelector' in document &&
                'addEventListener' in window &&
                'classList' in document.createElement('div') &&
                'requestAnimationFrame' in window;

            if (!isSupported) {
                alert('您的浏览器版本过低，可能无法正常显示页面效果。建议使用最新版本的Chrome、Safari或Firefox浏览器。');
            }
        }

        // 预加载关键资源
        function preloadResources() {
            // 预加载字体
            const fontLink = document.createElement('link');
            fontLink.rel = 'preload';
            fontLink.href = 'https://fonts.googleapis.com/css2?family=Ma+Shan+Zheng&family=Noto+Sans+SC:wght@300;400;500;700&display=swap';
            fontLink.as = 'style';
            document.head.appendChild(fontLink);
        }

        // 初始化
        checkBrowserSupport();
        preloadResources();
        initPages();

        // 页面可见性变化处理
        document.addEventListener('visibilitychange', function() {
            if (document.hidden) {
                // 页面隐藏时暂停动画
                document.querySelectorAll('.floating-heart, .confetti').forEach(el => {
                    el.style.animationPlayState = 'paused';
                });
            } else {
                // 页面显示时恢复动画
                document.querySelectorAll('.floating-heart, .confetti').forEach(el => {
