   626	<script>
   627	    document.addEventListener('DOMContentLoaded', function() {
   628	        const pages = document.querySelectorAll('.page');
   629	        const prevBtn = document.getElementById('prev-btn');
   630	        const nextBtn = document.getElementById('next-btn');
   631	        const progress = document.getElementById('progress');
   632	        const currentPageEl = document.getElementById('current-page');
   633	        const totalPagesEl = document.getElementById('total-pages');
   634	
   635	        let currentPage = 1;
   636	        const totalPages = pages.length;
   637	
   638	        totalPagesEl.textContent = totalPages;
   639	
   640	        // 创建心形动画
   641	        function createHearts(containerId, count) {
   642	            const container = document.getElementById(containerId);
   643	            const colors = ['#ff7eb9', '#7eb6ff', '#ffd97e'];
   644	            const symbols = ['❤️', '', '', '', ''];
   645	
   646	            for (let i = 0; i < count; i++) {
   647	                const heart = document.createElement('div');
   648	                heart.classList.add('heart');
   649	                heart.textContent = symbols[Math.floor(Math.random() * symbols.length)];
   650	                heart.style.left = `${Math.random() * 100}%`;
   651	                heart.style.top = `${Math.random() * 100}%`;
   652	                heart.style.animationDelay = `${Math.random() * 3}s`;
   653	                heart.style.opacity = '0';
   654	                container.appendChild(heart);
   655	            }
   656	        }
   657	
   658	        // 创建爱的表达背景
   659	        function createLoveBackground() {
   660	            const container = document.getElementById('love-bg-container');
   661	            const loveMessages = [
   662	                { text: "我永远爱你 唐婷", person: "xiaolu" },
   663	                { text: "我也永远爱你 胡正坤！", person: "tangbao" },
   664	                { text: "好爱你宝宝", person: "xiaolu" },
   665	                { text: "我也爱你宝宝", person: "tangbao" },
   666	                { text: "爱你爱你", person: "xiaolu" },
   667	                { text: "我也爱你", person: "tangbao" },
   668	                { text: "爱你爱你爱你", person: "xiaolu" },
   669	                { text: "希望不仅在口头说爱你", person: "tangbao" },
   670	                { text: "也在行动上爱你", person: "tangbao" },
   671	                { text: "胡正坤 我也爱你", person: "tangbao" },
   672	                { text: "我爱你", person: "xiaolu" },
   673	                { text: "我也很爱很爱你", person: "tangbao" },
   674	                { text: "唐婷 爱你爱你爱你", person: "xiaolu" },
   675	                { text: "宝宝 我好爱你", person: "tangbao" }
   676	            ];
   677	
   678	            for (let i = 0; i < loveMessages.length; i++) {
   679	                const message = document.createElement('div');
   680	                message.classList.add('love-message');
   681	                message.classList.add(loveMessages[i].person);
   682	                message.textContent = loveMessages[i].text;
   683	                message.style.left = `${Math.random() * 80 + 10}%`;
   684	                message.style.top = `${Math.random() * 80 + 10}%`;
   685	                message.style.transform = `rotate(${Math.random() * 20 - 10}deg)`;
   686	                message.style.fontSize = `${Math.random() * 0.5 + 0.8}rem`;
   687	                message.style.transitionDelay = `${Math.random() * 2}s`;
   688	                container.appendChild(message);
   689	            }
   690	        }
   691	
   692	        // 创建想你背景
