   722	        createHearts('heart-container', 15);
   723	        createHearts('heart-container-2', 15);
   724	        createHearts('heart-container-3', 15);
   725	        createLoveBackground();
   726	        createMissBackground();
   727	
   728	        // 初始化第一页的动画
   729	        animatePage(1);
   730	
   731	        // 更新进度条
   732	        updateProgress();
   733	
   734	        // 下一页
   735	        nextBtn.addEventListener('click', function() {
   736	            if (currentPage < totalPages) {
   737	                changePage(currentPage + 1);
   738	            }
   739	        });
   740	
   741	        // 上一页
   742	        prevBtn.addEventListener('click', function() {
   743	            if (currentPage > 1) {
   744	                changePage(currentPage - 1);
   745	            }
   746	        });
   747	
   748	        // 切换页面
   749	        function changePage(pageNum) {
   750	            // 获取当前页和目标页
   751	            const currentPageEl = pages[currentPage - 1];
   752	            const targetPageEl = pages[pageNum - 1];
   753	
   754	            // 根据方向设置类名
   755	            if (pageNum > currentPage) {
   756	                // 向前翻页
   757	                currentPageEl.classList.remove('active');
   758	                currentPageEl.classList.add('prev');
   759	                targetPageEl.classList.remove('prev');
   760	                targetPageEl.classList.add('active');
   761	            } else {
   762	                // 向后翻页
   763	                currentPageEl.classList.remove('active');
   764	                targetPageEl.classList.remove('prev');
   765	
   766	                // 延迟一下，确保过渡效果正确
   767	                setTimeout(() => {
   768	                    targetPageEl.classList.add('active');
   769	                }, 50);
   770	            }
   771	
   772	            // 更新当前页
   773	            currentPage = pageNum;
   774	            currentPageEl.textContent = currentPage;
   775	
   776	            // 更新进度条
   777	            updateProgress();
   778	
   779	            // 触发当前页的动画
   780	            animatePage(currentPage);
   781	        }
   782	
   783	        // 更新进度条
   784	        function updateProgress() {
   785	            const progressPercent = ((currentPage - 1) / (totalPages - 1)) * 100;
