   927	
   928	        // 键盘导航
   929	        document.addEventListener('keydown', function(e) {
   930	            if (e.key === 'ArrowRight' || e.key === ' ') {
   931	                if (currentPage < totalPages) {
   932	                    changePage(currentPage + 1);
   933	                }
   934	            } else if (e.key === 'ArrowLeft') {
   935	                if (currentPage > 1) {
   936	                    changePage(currentPage - 1);
   937	                }
   938	            }
   939	        });
   940	
   941	        // 触摸滑动导航
   942	        let touchStartX = 0;
   943	        let touchEndX = 0;
   944	
   945	        document.addEventListener('touchstart', function(e) {
   946	            touchStartX = e.changedTouches[0].screenX;
   947	        });
   948	
   949	        document.addEventListener('touchend', function(e) {
   950	            touchEndX = e.changedTouches[0].screenX;
   951	            handleSwipe();
   952	        });
   953	
   954	        function handleSwipe() {
   955	            const swipeThreshold = 50;
   956	            if (touchEndX < touchStartX - swipeThreshold) {
   957	                // 左滑
   958	                if (currentPage < totalPages) {
   959	                    changePage(currentPage + 1);
   960	                }
   961	            } else if (touchEndX > touchStartX + swipeThreshold) {
   962	                // 右滑
   963	                if (currentPage > 1) {
   964	                    changePage(currentPage - 1);
   965	                }
   966	            }
   967	        }
   968	    });
