   198	    // 预加载图片逻辑
   199	    const loadedImages = ref(new Set())
   200	    const failedImages = ref(new Set())
   201	
   202	    const preloadImages = () => {
   203	      const allImageUrls = memories.value.flatMap(memory => memory.images)
   204	      allImageUrls.forEach(url => {
   205	        const img = new Image()
   206	        img.onload = () => {
   207	          loadedImages.value = new Set([...loadedImages.value, url])
   208	        }
   209	        img.onerror = () => {
   210	          failedImages.value = new Set([...failedImages.value, url])
   211	        }
   212	        img.src = url
   213	      })
   214	    }
   215	
