   290	  const { createApp, ref, onMounted, onUnmounted, nextTick } = Vue;
   291	
   292	  createApp({
   293	    setup() {
   294	      // 响应式数据
   295	      const showError = ref(false);
   296	      const showText = ref(false);
   297	      const showClock = ref(false);
   298	      const showHand = ref(false);
   299	      const audioPlaying = ref(false);
   300	      const timeDisplay = ref('');
   301	
   302	      // DOM引用
   303	      const canvas = ref(null);
   304	      const textElement = ref(null);
   305	      const bgMusic = ref(null);
   306	
   307	      // 动画相关变量
   308	      let loveAnimation = null;
   309	      let timeInterval = null;
   310	
   311	      // 相恋开始时间
   312	      const loveStartDate = new Date(2014, 5, 15, 20, 0, 0, 2);
   313	
   314	      // 检查Canvas支持
   315	      const checkCanvasSupport = () => {
   316	        const testCanvas = document.createElement('canvas');
   317	        return !!(testCanvas.getContext && testCanvas.getContext('2d'));
   318	      };
   319	
   320	      // 初始化Canvas
   321	      const initCanvas = () => {
   322	        if (!canvas.value || !checkCanvasSupport()) {
   323	          showError.value = true;
   324	          return false;
   325	        }
   326	
   327	        const canvasEl = canvas.value;
   328	        const container = canvasEl.parentElement;
   329	        const containerRect = container.getBoundingClientRect();
   330	
   331	        // 响应式Canvas尺寸
   332	        const isMobile = window.innerWidth <= 768;
   333	        const canvasWidth = isMobile ? containerRect.width : Math.min(1100, containerRect.width);
   334	        const canvasHeight = isMobile ? containerRect.height : Math.min(680, containerRect.height);
   335	
   336	        canvasEl.width = canvasWidth;
   337	        canvasEl.height = canvasHeight;
   338	        canvasEl.style.width = canvasWidth + 'px';
   339	        canvasEl.style.height = canvasHeight + 'px';
   340	
   341	        // 初始化爱心树动画
   342	        if (window.LoveTreeAnimation) {
   343	          loveAnimation = new window.LoveTreeAnimation(canvasEl, {
   344	            heartColor: 'rgb(190, 26, 37)',
   345	            bloomCount: isMobile ? 200 : 500,
   346	            animationSpeed: isMobile ? 25 : 15
   347	          });
   348	        }
   349	
   350	        return { width: canvasWidth, height: canvasHeight };
   351	      };
   352	
   353	      // 文字打字机效果
   354	      const typewriterEffect = async () => {
   355	        if (!textElement.value) return;
   356	
   357	        showText.value = true;
   358	        await nextTick();
   359	
   360	        const element = textElement.value;
   361	        const originalHTML = element.innerHTML;
   362	        element.innerHTML = '';
   363	
   364	        let progress = 0;
   365	        const typeInterval = setInterval(() => {
   366	          let current = originalHTML.substr(progress, 1);
   367	          if (current === '<') {
   368	            progress = originalHTML.indexOf('>', progress) + 1;
   369	          } else {
   370	            progress++;
   371	          }
   372	
   373	          element.innerHTML = originalHTML.substring(0, progress) +
   374	              (progress & 1 ? '<span class="typewriter-cursor">_</span>' : '');
   375	
   376	          if (progress >= originalHTML.length) {
   377	            clearInterval(typeInterval);
   378	            element.innerHTML = originalHTML;
   379	          }
   380	        }, 75);
   381	      };
   382	
   383	      // 时间计算
   384	      const updateTimeDisplay = () => {
   385	        const now = new Date();
   386	        const timeDiff = now.getTime() - loveStartDate.getTime();
   387	        const seconds = Math.floor(timeDiff / 1000);
   388	
   389	        const days = Math.floor(seconds / (3600 * 24));
   390	        const hours = Math.floor((seconds % (3600 * 24)) / 3600);
   391	        const minutes = Math.floor((seconds % 3600) / 60);
   392	        const secs = seconds % 60;
   393	
   394	        const formatTime = (num) => num.toString().padStart(2, '0');
   395	
   396	        timeDisplay.value = `第 <span class="digit">${days}</span> 天 <span class="digit">${formatTime(hours)}</span> 小时 <span class="digit">${formatTime(minutes)}</span> 分钟 <span class="digit">${formatTime(secs)}</span> 秒`;
   397	      };
   398	
   399	      // 音频控制
   400	      const toggleAudio = async () => {
   401	        if (!bgMusic.value) return;
   402	
   403	        try {
   404	          if (audioPlaying.value) {
   405	            bgMusic.value.pause();
   406	            audioPlaying.value = false;
   407	          } else {
   408	            await bgMusic.value.play();
   409	            audioPlaying.value = true;
   410	          }
   411	        } catch (error) {
   412	          console.log('音频播放失败:', error);
   413	        }
   414	      };
   415	
   416	      // Canvas点击处理
   417	      const handleCanvasClick = (event) => {
   418	        if (!loveAnimation) return;
   419	
   420	        const rect = canvas.value.getBoundingClientRect();
   421	        const x = event.clientX - rect.left;
   422	        const y = event.clientY - rect.top;
   423	
   424	        // 在点击位置创建爱心爆炸效果
   425	        loveAnimation.handleClick(x, y);
   426	      };
   427	
   428	      // 移动端触摸处理
   429	      const handleCanvasTouch = (event) => {
   430	        event.preventDefault();
   431	        const touch = event.touches[0];
   432	        const rect = canvas.value.getBoundingClientRect();
   433	        const x = touch.clientX - rect.left;
   434	        const y = touch.clientY - rect.top;
   435	
   436	        // 触摸位置创建爱心效果
   437	        if (loveAnimation) {
   438	          loveAnimation.handleClick(x, y);
   439	        }
   440	      };
   441	
   442	      // 窗口大小变化处理
   443	      const handleResize = () => {
   444	        if (canvas.value && loveAnimation) {
   445	          const canvasEl = canvas.value;
   446	          const container = canvasEl.parentElement;
   447	          const containerRect = container.getBoundingClientRect();
   448	
   449	          const isMobile = window.innerWidth <= 768;
   450	          const canvasWidth = isMobile ? containerRect.width : Math.min(1100, containerRect.width);
   451	          const canvasHeight = isMobile ? containerRect.height : Math.min(680, containerRect.height);
   452	
   453	          // 调整动画尺寸
   454	          loveAnimation.resize(canvasWidth, canvasHeight);
   455	        }
   456	      };
   457	
   458	      // 组件挂载
   459	      onMounted(async () => {
   460	        // 初始化Canvas
   461	        const canvasSize = initCanvas();
   462	        if (!canvasSize) return;
   463	
   464	        // 延迟启动动画序列
   465	        setTimeout(async () => {
   466	          // 启动Canvas动画
   467	          if (loveAnimation) {
   468	            loveAnimation.start();
   469	          }
