art => {\n      this.ctx.globalAlpha = heart.opacity\n      this.drawHeart(heart.x, heart.y, heart.size)\n      \n      // \u66f4\u65b0\u4f4d\u7f6e\n      heart.y -= heart.speed\n      heart.x += Math.sin(heart.angle) * 0.5\n      heart.angle += 0.02\n      \n      // \u91cd\u7f6e\u4f4d\u7f6e\n      if (heart.y < -20) {\n        heart.y = this.canvas.height + 20\n        heart.x = Math.random() * this.canvas.width\n      }\n    })\n\n    this.ctx.globalAlpha = 1\n    this.animationId = requestAnimationFrame(() => this.animate())\n  }\n\n  handleClick(x, y) {\n    // \u5728\u70b9\u51fb\u4f4d\u7f6e\u6dfb\u52a0\u7206\u70b8\u6548\u679c\n    for (let i = 0; i < 10; i++) {\n      this.hearts.push({\n        x: x + (Math.random() - 0.5) * 50,\n        y: y + (Math.random() - 0.5) * 50,\n        size: Math.random() * 2 + 1,\n        opacity: 1,\n        speed: Math.random() * 3 + 2,\n        angle: Math.random() * Math.PI * 2\n      })\n    }\n  }\n\n  resize(width, height) {\n    this.canvas.width = width\n    this.canvas.height = height\n    this.canvas.style.width = width + 'px'\n    this.canvas.style.height = height + 'px'\n    this.generateHearts()\n  }\n}\n\n// \u521d\u59cb\u5316Canvas\nconst initCanvas = () => {\n  if (!canvas.value || !checkCanvasSupport()) {\n    showError.value = true\n    return false\n  }\n\n  const canvasEl = canvas.value\n  const container = canvasEl.parentElement\n  \n  if (!container) {\n    console.error('Canvas container not found')\n    showError.value = true\n    return false\n  }\n\n  const containerRect = container.getBoundingClientRect()\n\n  // \u54cd\u5e94\u5f0fCanvas\u5c3a\u5bf8\n  const isMobile = window.innerWidth <= 768\n  const canvasWidth = isMobile ? Math.max(300, containerRect.width) : Math.min(1100, Math.max(800, containerRect.width))\n  const canvasHeight = isMobile ? Math.max(400, containerRect.height) : Math.min(680, Math.max(500, containerRect.height))\n\n  canvasEl.width = canvasWidth\n  canvasEl.height = canvasHeight\