   642	
   643	      const containerRect = container.getBoundingClientRect()
   644	
   645	      const isMobile = window.innerWidth <= 768
   646	      const canvasWidth = isMobile ? Math.max(300, containerRect.width) : Math.min(1100, Math.max(800, containerRect.width))
   647	      const canvasHeight = isMobile ? Math.max(400, containerRect.height) : Math.min(680, Math.max(500, containerRect.height))
   648	
   649	      // 调整动画尺寸
   650	      loveAnimation.resize(canvasWidth, canvasHeight)
   651	    } catch (error) {
   652	      console.error('Error during resize:', error)
   653	    }
   654	  }
   655	}
   656	
   657	// 组件挂载
   658	onMounted(async () => {
   659	  try {
   660	    // 等待DOM完全渲染
   661	    await nextTick()
   662	
   663	    // 初始化Canvas
   664	    const canvasSize = initCanvas()
   665	    if (!canvasSize) return
