首先重新设计MemoryCard组件：

````vue path=src/components/Memory/MemoryCard.vue mode=EDIT
<template>
  <div class="memory-card">
    <!-- 图片区域 -->
    <div class="image-container" @click="toggleDescription">
      <!-- 图片轮播 -->
      <div class="image-wrapper">
        <img
          :src="currentImage"
          :alt="memory.title"
          class="memory-image"
          @error="handleImageError"
          v-if="!imageError"
        />
        <!-- 图片加载失败占位 -->
        <div v-else class="image-placeholder">
          <div class="placeholder-icon"></div>
          <div class="placeholder-text">图片加载失败</div>
        </div>
      </div>

      <!-- 多图片导航 -->
      <template v-if="hasMultipleImages">
        <!-- 左右切换按钮 -->
        <button
          class="nav-btn nav-btn-left"
          @click.stop="prevImage"
          v-show="memory.images.length > 1"
        >
          ‹
        </button>
        <button
          class="nav-btn nav-btn-right"
          @click.stop="nextImage"
          v-show="memory.images.length > 1"
        >
          ›
        </button>

        <!-- 图片指示器 -->
        <div class="image-indicators">
          <span
            v-for="(_, index) in memory.images"
            :key="index"
            class="indicator"
            :class="{ active: index === currentImageIndex }"
            @click.stop="setCurrentImage(index)"
          ></span>
        </div>

        <!-- 图片数量标识 -->
        <div class="image-count">
          {{ currentImageIndex + 1 }}/{{ memory.images.length }}
        </div>
      </template>
    </div>

    <!-- 信息区域 -->
    <div class="info-container">
      <!-- 基本信息（始终显示） -->
      <div class="basic-info">
        <h3 class="title">{{ memory.title }}</h3>
        <div class="meta-info">
          <span class="date"> {{ memory.date }}</span>
          <span class="location"> {{ memory.location }}</span>
        </div>
        <span class="category">{{ memory.category }}</span>
      </div>

      <!-- 描述区域（可展开/收起） -->
      <transition name="description">
        <div v-if="showDescription" class="description">
          <p>{{ memory.description }}</p>
        </div>
      </transition>

      <!-- 点击提示 -->
      <div class="click-hint" v-if="!showDescription">
        点击图片查看详情
      </div>
    </div>
  </div>
</template>

<script setup>
import { ref, computed } from 'vue'

const props = defineProps({
  memory: {
    type: Object,
    required: true
  }
})

const currentImageIndex = ref(0)
const showDescription = ref(false)
const imageError = ref(false)

const currentImage = computed(() => {
  return props.memory.images[currentImageIndex.value]
})

const hasMultipleImages = computed(() => {
  return props.memory.images && props.memory.images.length > 1
})

const toggleDescription = () => {
  showDescription.value = !showDescription.value
}

const nextImage = () => {
  if (hasMultipleImages.value) {
    currentImageIndex.value = (currentImageIndex.value + 1) % props.memory.images.length
  }
}

const prevImage = () => {
  if (hasMultipleImages.value) {
    currentImageIndex.value = (currentImageIndex.value - 1 + props.memory.images.length) % props.memory.images.length
  }
}

const setCurrentImage = (index) => {
  currentImageIndex.value = index
}

const handleImageError = () => {
  imageError.value = true
}
</script>

