          @click="setActiveCategory(category.key)"
      >
        {{ category.label }}
      </button>
    </div>

    <!-- 记忆列表容器 -->
    <div class="memories-container">
      <div class="memories-list">
        <MemoryCard
            v-for="memory in filteredMemories"
            :key="memory.id"
            :memory="memory"
        />
      </div>
    </div>
  </div>
</template>

