   714	                message.style.top = `${Math.random() * 80 + 10}%`;
   715	                message.style.transform = `rotate(${Math.random() * 20 - 10}deg)`;
   716	                message.style.fontSize = `${Math.random() * 0.5 + 0.8}rem`;
