   578	          if (audioPlaying.value) {
   579	            bgMusic.value.pause();
   580	            audioPlaying.value = false;
   581	          } else {
   582	            await bgMusic.value.play();
   583	            audioPlaying.value = true;
   584	          }
   585	        } catch (error) {
   586	          console.log('音频播放失败:', error);
   587	        }
   588	      };
   589	
   590	      // Canvas点击处理
   591	      const handleCanvasClick = (event) => {
   592	        if (!loveAnimation) return;
   593	
   594	        const rect = canvas.value.getBoundingClientRect();
   595	        const x = event.clientX - rect.left;
   596	        const y = event.clientY - rect.top;
   597	
   598	        // 在点击位置创建爱心爆炸效果
   599	        loveAnimation.handleClick(x, y);
   600	      };
   601	
   602	      // 移动端触摸处理
   603	      const handleCanvasTouch = (event) => {
   604	        event.preventDefault();
   605	        const touch = event.touches[0];
   606	        const rect = canvas.value.getBoundingClientRect();
   607	        const x = touch.clientX - rect.left;
   608	        const y = touch.clientY - rect.top;
   609	
   610	        // 触摸位置创建爱心效果
   611	        if (loveAnimation) {
   612	          loveAnimation.handleClick(x, y);
   613	        }
   614	      };
   615	
   616	      // 窗口大小变化处理
   617	      const handleResize = () => {
   618	        if (canvas.value && loveAnimation) {
   619	          const canvasEl = canvas.value;
   620	          const container = canvasEl.parentElement;
   621	          const containerRect = container.getBoundingClientRect();
   622	
   623	          const isMobile = window.innerWidth <= 768;
   624	          const canvasWidth = isMobile ? containerRect.width : Math.min(1100, containerRect.width);
   625	          const canvasHeight = isMobile ? containerRect.height : Math.min(680, containerRect.height);
   626	
   627	          // 调整动画尺寸
   628	          loveAnimation.resize(canvasWidth, canvasHeight);
   629	        }
   630	      };
   631	
   632	      // 组件挂载
   633	      onMounted(async () => {
   634	        // 初始化Canvas
   635	        const canvasSize = initCanvas();
   636	        if (!canvasSize) return;
   637	
   638	        // 延迟启动动画序列
   639	        setTimeout(async () => {
   640	          // 启动Canvas动画
   641	          if (loveAnimation) {
   642	            loveAnimation.start();
   643	          }
   644	
   645	          // 开始文字动画
   646	          await typewriterEffect();
   647	
   648	          // 显示时钟
   649	          showClock.value = true;
   650	
   651	          // 开始时间更新
   652	          updateTimeDisplay();
   653	          timeInterval = setInterval(updateTimeDisplay, 1000);
   654	
   655	          // 尝试自动播放音乐（可能被浏览器阻止）
   656	          try {
   657	            await bgMusic.value.play();
   658	            audioPlaying.value = true;
   659	          } catch (error) {
   660	            console.log('自动播放被阻止，需要用户交互');
   661	          }
   662	        }, 1000);
   663	
   664	        // 监听窗口大小变化
   665	        window.addEventListener('resize', handleResize);
   666	      });
   667	
   668	      // 组件卸载
   669	      onUnmounted(() => {
   670	        if (timeInterval) {
   671	          clearInterval(timeInterval);
   672	        }
   673	        if (loveAnimation) {
   674	          loveAnimation.stop();
   675	        }
   676	        window.removeEventListener('resize', handleResize);
   677	      });
