   131	<script>
   132	import { ref, computed, reactive, onMounted, onUnmounted } from 'vue'
   133	import { ChevronLeft, ChevronRight, MapPin, Calendar, Camera, Image as ImageIcon } from 'lucide-vue-next'
   134	
   135	export default {
   136	  components: {
   137	    ChevronLeft,
   138	    ChevronRight,
   139	    MapPin,
   140	    Calendar,
   141	    Camera,
   142	    ImageIcon
   143	  },
   144	  props: {
   145	    memory: Object,
   146	    index: Number,
   147	    loadedImages: Set,
   148	    failedImages: Set
   149	  },
   150	  setup(props) {
   151	    const currentImageIndex = ref(0)
   152	    const showDetails = ref(false)
   153	    const imageError = ref(false)
   154	    const isMobile = ref(false)
   155	    const touchStartTime = ref(0)
   156	
   157	    const currentImage = computed(() => props.memory.images[currentImageIndex.value])
   158	    const hasMultipleImages = computed(() => props.memory.images.length > 1)
   159	    const isImageLoaded = computed(() => props.loadedImages.has(currentImage.value))
   160	    const isImageFailed = computed(() => props.failedImages.has(currentImage.value) || imageError.value)
   161	
   162	    const nextImage = () => {
   163	      if (hasMultipleImages.value) {
   164	        currentImageIndex.value = (currentImageIndex.value + 1) % props.memory.images.length
   165	      }
   166	    }
   167	
   168	    const prevImage = () => {
   169	      if (hasMultipleImages.value) {
   170	        currentImageIndex.value = (currentImageIndex.value - 1 + props.memory.images.length) % props.memory.images.length
   171	      }
   172	    }
   173	
   174	    const setCurrentImageIndex = (index) => {
   175	      currentImageIndex.value = index
   176	    }
   177	
   178	    const handleImageError = () => {
   179	      imageError.value = true
   180	    }
   181	
   182	    // 移动端检测和触摸处理
   183	    const checkMobile = () => {
   184	      isMobile.value = window.innerWidth <= 768 || 'ontouchstart' in window
   185	    }
   186	
   187	    const handleTouchStart = () => {
   188	      touchStartTime.value = Date.now()
   189	      if (isMobile.value) {
   190	        showDetails.value = true
   191	      }
   192	    }
   193	
   194	    const handleTouchEnd = () => {
   195	      const touchDuration = Date.now() - touchStartTime.value
   196	      if (touchDuration > 300 && isMobile.value) {
   197	        // 长按保持显示详情
   198	        return
   199	      }
   200	      // 短按切换详情显示
   201	      if (isMobile.value && touchDuration < 300) {
   202	        showDetails.value = !showDetails.value
   203	      }
   204	    }
   205	
   206	    // 监听窗口大小变化
   207	    const handleResize = () => {
   208	      checkMobile()
   209	    }
   210	
   211	    // 组件挂载时检测移动端
   212	    onMounted(() => {
   213	      checkMobile()
   214	      window.addEventListener('resize', handleResize)
   215	    })
   216	
   217	    // 组件卸载时清理事件监听
   218	    onUnmounted(() => {
   219	      window.removeEventListener('resize', handleResize)
   220	    })
   221	
   222	    // 动画样式
   223	    const cardStyle = reactive({
   224	      opacity: 0,
   225	      transform: 'translateY(50px)',
   226	      animation: `slideUp 0.6s ${props.index * 0.1}s forwards`
   227	    })
   228	
   229	    const imageStyle = reactive({
   230	      opacity: 0,
   231	      transform: 'scale(1.1)',
   232	      animation: 'fadeScaleIn 0.5s forwards'
   233	    })
   234	
   235	    const titleStyle = reactive({
   236	      opacity: 0,
   237	      animation: 'fadeIn 0.5s 0.2s forwards'
   238	    })
   239	
   240	    const metaStyle = reactive({
   241	      opacity: 0,
   242	      animation: 'fadeIn 0.5s 0.3s forwards'
   243	    })
   244	
   245	    const categoryStyle = {
   246	      '--hover-scale': 1.05
   247	    }
   248	
   249	    const navButtonStyle = (position) => {
   250	      return {
   251	        opacity: showDetails.value ? 1 : 0,
   252	        transition: 'opacity 0.3s',
   253	        [position === 'left' ? 'left' : 'right']: '0.5rem'
   254	      }
   255	    }
   256	
   257	    return {
   258	      currentImageIndex,
   259	      showDetails,
   260	      currentImage,
   261	      hasMultipleImages,
   262	      isImageLoaded,
   263	      isImageFailed,
   264	      isMobile,
   265	      nextImage,
   266	      prevImage,
   267	      setCurrentImageIndex,
   268	      handleImageError,
   269	      handleTouchStart,
   270	      handleTouchEnd,
   271	      cardStyle,
   272	      imageStyle,
   273	      titleStyle,
   274	      metaStyle,
   275	      categoryStyle,
   276	      navButtonStyle
   277	    }
   278	  }
   279	}
   280	</script>
