     3	//显示Loading
     4	(function($){
     5		$.fn.loading = function(options){
     6			var $this = $(this);
     7			var _this = this;
     8			return this.each(function(){
     9			    var loadingPosition ='';
    10			    var defaultProp = {
    11			    	direction: 				'column',												//方向，column纵向   row 横向
    12					animateIn: 	 			'fadeInNoTransform',    								//进入类型
