    18					discColor: 				'rgba(255,255,255,0.7)',								//disc文本颜色
    19					loadingWidth:           260,                									//中间的背景宽度width
    20					loadingBg:        		'rgba(0, 0, 0, 0.6)',  									//中间的背景色
    21					borderRadius:     		12,                 									//中间的背景色的borderRadius
