  1370	    
  1371	    var isCommonJS = (typeof require !== "undefined" && typeof module !== "undefined" && module.exports);
  1372	    var isAmd = (typeof define !== "undefined" && define.amd);
  1373	    
  1374	    if (isCommonJS) {
  1375	        module.exports.init = function (root) {
  1376	            if (!root.modules["parser"]) {
  1377	                require("./jscex-parser").init(root);
  1378	            };
  1379	            
  1380	            init(root);
  1381	        }
  1382	    } else if (isAmd) {
  1383	        define("jscex-jit", ["jscex-parser"], function (parser) {
  1384	            return {
  1385	                init: function (root) {
  1386	                    if (!root.modules["parser"]) {
  1387	                        parser.init(root);
  1388	                    }
  1389	                    
  1390	                    init(root);
  1391	                }
  1392	            };
  1393	        });
  1394	    } else {
  1395	        if (typeof Jscex === "undefined") {
  1396	            throw new Error('Missing root object, please load "jscex" module first.');
  1397	        }
  1398	        
  1399	        if (!Jscex.modules["parser"]) {
  1400	            throw new Error('Missing essential components, please initialize "parser" module first.');
  1401	        }
  1402	
  1403	        init(Jscex);
  1404	    }
  1405	
